function createCookie(e,t,n){if(n){var r=new Date;r.setTime(r.getTime()+n*24*60*60*1e3);var i="; expires="+r.toGMTString()}else var i="";document.cookie=e+"="+t+i+"; path=/"}function readCookie(e){var t=e+"=",n=document.cookie.split(";");for(var r=0;r<n.length;r++){var i=n[r];while(i.charAt(0)==" ")i=i.substring(1,i.length);if(i.indexOf(t)==0)return i.substring(t.length,i.length)}return null}function eraseCookie(e){createCookie(e,"",-1)}show_openid=function(){$("#openid").show(),$("#standard").hide(),$("#openid_link").hide(),$("#standard_link").show(),createCookie("use_openid",1,30),$("#openid_url").value="http://"},show_standard=function(){$("#openid").hide(),$("#standard").show(),$("#openid_link").show(),$("#standard_link").hide(),eraseCookie("use_openid"),$("#openid_url").value=""},$(function(){$("#openid").hide(),$("#standard_link").hide(),$("#noscript").show(),readCookie("use_openid")&&show_openid()});